{
   "title": "HuggingFaceEmbedding",
   "description": "Base class for embeddings.",
   "type": "object",
   "properties": {
      "model\_name": {
         "title": "Model Name",
         "description": "The name of the embedding model.",
         "default": "unknown",
         "type": "string"
      },
      "embed\_batch\_size": {
         "title": "Embed Batch Size",
         "description": "The batch size for embedding calls.",
         "default": 10,
         "type": "integer"
      },
      "callback\_manager": {
         "title": "Callback Manager"
      },
      "tokenizer\_name": {
         "title": "Tokenizer Name",
         "description": "Tokenizer name from HuggingFace.",
         "type": "string"
      },
      "max\_length": {
         "title": "Max Length",
         "description": "Maximum length of input.",
         "type": "integer"
      },
      "pooling": {
         "title": "Pooling",
         "description": "Pooling strategy. One of \['cls', 'mean'\].",
         "type": "string"
      },
      "normalize": {
         "title": "Normalize",
         "description": "Normalize embeddings or not.",
         "default": true,
         "type": "string"
      },
      "query\_instruction": {
         "title": "Query Instruction",
         "description": "Instruction to prepend to query text.",
         "type": "string"
      },
      "text\_instruction": {
         "title": "Text Instruction",
         "description": "Instruction to prepend to text.",
         "type": "string"
      },
      "cache\_folder": {
         "title": "Cache Folder",
         "description": "Cache folder for huggingface files.",
         "type": "string"
      }
   },
   "required": \[
      "tokenizer\_name",
      "max\_length",
      "pooling"
   \]
}
