{
   "title": "SubQuestionAnswerPair",
   "description": "Pair of the sub question and optionally its answer (if its been answered yet).",
   "type": "object",
   "properties": {
      "sub\_q": {
         "$ref": "#/definitions/SubQuestion"
      },
      "answer": {
         "title": "Answer",
         "type": "string"
      },
      "sources": {
         "title": "Sources",
         "type": "array",
         "items": {
            "$ref": "#/definitions/NodeWithScore"
         }
      }
   },
   "required": \[
      "sub\_q"
   \],
   "definitions": {
      "SubQuestion": {
         "title": "SubQuestion",
         "type": "object",
         "properties": {
            "sub\_question": {
               "title": "Sub Question",
               "type": "string"
            },
            "tool\_name": {
               "title": "Tool Name",
               "type": "string"
            }
         },
         "required": \[
            "sub\_question",
            "tool\_name"
         \]
      },
      "ObjectType": {
         "title": "ObjectType",
         "description": "An enumeration.",
         "enum": \[
            "1",
            "2",
            "3",
            "4"
         \],
         "type": "string"
      },
      "RelatedNodeInfo": {
         "title": "RelatedNodeInfo",
         "description": "Base component object to capture class names.",
         "type": "object",
         "properties": {
            "node\_id": {
               "title": "Node Id",
               "type": "string"
            },
            "node\_type": {
               "$ref": "#/definitions/ObjectType"
            },
            "metadata": {
               "title": "Metadata",
               "type": "object"
            },
            "hash": {
               "title": "Hash",
               "type": "string"
            }
         },
         "required": \[
            "node\_id"
         \]
      },
      "BaseNode": {
         "title": "BaseNode",
         "description": "Base node Object.\\n\\nGeneric abstract interface for retrievable nodes",
         "type": "object",
         "properties": {
            "id\_": {
               "title": "Id ",
               "description": "Unique ID of the node.",
               "type": "string"
            },
            "embedding": {
               "title": "Embedding",
               "description": "Embedding of the node.",
               "type": "array",
               "items": {
                  "type": "number"
               }
            },
            "extra\_info": {
               "title": "Extra Info",
               "description": "A flat dictionary of metadata fields",
               "type": "object"
            },
            "excluded\_embed\_metadata\_keys": {
               "title": "Excluded Embed Metadata Keys",
               "description": "Metadata keys that are excluded from text for the embed model.",
               "type": "array",
               "items": {
                  "type": "string"
               }
            },
            "excluded\_llm\_metadata\_keys": {
               "title": "Excluded Llm Metadata Keys",
               "description": "Metadata keys that are excluded from text for the LLM.",
               "type": "array",
               "items": {
                  "type": "string"
               }
            },
            "relationships": {
               "title": "Relationships",
               "description": "A mapping of relationships to other node information.",
               "type": "object",
               "additionalProperties": {
                  "anyOf": \[
                     {
                        "$ref": "#/definitions/RelatedNodeInfo"
                     },
                     {
                        "type": "array",
                        "items": {
                           "$ref": "#/definitions/RelatedNodeInfo"
                        }
                     }
                  \]
               }
            },
            "hash": {
               "title": "Hash",
               "description": "Hash of the node content.",
               "default": "",
               "type": "string"
            }
         }
      },
      "NodeWithScore": {
         "title": "NodeWithScore",
         "description": "Base component object to capture class names.",
         "type": "object",
         "properties": {
            "node": {
               "$ref": "#/definitions/BaseNode"
            },
            "score": {
               "title": "Score",
               "type": "number"
            }
         },
         "required": \[
            "node"
         \]
      }
   }
}
